import React from 'react';

class Search extends React.Component{
    state = {
        say: ""
    }

    render(){
        return(
            <div>
                <input type="text" placeholder="search" id="input-id"/>
                <h1>{this.state.say}</h1>
                <input style={this.val()} type= "button" value="submit"/>
            </div>


        );
    }
    val(){

        return this.state.say === "no" ? {color : "red"} : {color : "blue"};
    }
}
export default Search;
